from pygments.style import Style
from pygments.token import Comment, Name, Keyword, String
from pygments.token import Keyword, Name, Comment, String, Error, \
        Number, Operator, Generic, Token, Whitespace


class MarsloStyle(Style):
    """
    Minimalistic "marslo" theme, based on Zap and Emacs defaults.
    """

    background_color = '#151515'
    highlight_color = '#0000ff'

    styles = {
        Token:              '#999999',
        Whitespace:         '#999999',

        Comment:            '#383838',
        Comment.Preproc:    '#e5e5e5',

        Keyword:            '#ff0000',
        Keyword.Type:       '#ee82ee',
        Operator.Word:      '#6ab825',
        # String:             '#87ceeb',
        String:             '#6ab825',
        Number:             '#0076ec',

        Name.Builtin:       '#447fcf',
        Name.Class:         'bold #6ab825',
        Name.Function:      '#ffff00',
        Name.Variable:      '#eedd82',
        Name.Constant:      '#7fffd4',
        Name.Exception:     '#bbbbbb',
        Name.Tag:           'underline #bbbbbb',
        Name.Attribute:     '#bbbbbb',
        Name.Decorator:     '#ffa500',

        Generic.Heading:    '#3677a9',
        Generic.Subheading: 'underline #3677a9',
        Generic.Deleted:    '#d22323',
        Generic.Inserted:   '#589819',
        Generic.Error:      '#d22323',
        Generic.Emph:       'italic',
        Generic.Strong:     'bold',
        Generic.Prompt:     '#ffa500',
        Generic.Output:     '#cccccc',
        Generic.Traceback:  '#d22323',

        Error:              'bg:#e3d2d2 #a61717'
    }
